
#!/bin/bash

# Challenge 'CipherQuest A' [Crypto]

a="  "

# B seems really suspicious so I assumed it was the space
b=$(echo $a | tr 'BA' ' *')

 echo $b
# then I started to figure the character out one by one
c=$(echo $b | tr 'xqf r t o p s z a w d g v i n j * h lyk' 'THE O W I N K C L F S R A U P Y D V MBG')

echo $c | sed -r 's/(.{10})/\1 /g' 
